mapper.select:
select * from 
	statistics_data_product where 1=1
	
		and create_user_id = #{createUserId}
	
	
		and member_id = #{memberId}
	
	
		and product_id = #{productId}
	
	
		and iPad_ip = #{ipadIp}
	
	
		and browse_time = #{browseTime}

select * from 
		statistics_data_product a where 1=1

	and a.create_time >= #{pd.query_time_start}


	and a.create_time <= #{pd.query_time_end}
	

	and a.name like concat('%',#{pd.query_key},'%')

	order by a.create_time desc

SELECT IFNULL(COUNT(b.year),'0') amount FROM ( select DATE_FORMAT(a.browse_time,'%Y') 'year' from statistics_product_browse a where DATE_FORMAT(a.browse_time,'%Y') = #{year}
and a.member_id = #{memberId}
and a.product_id = #{productId}
)b GROUP BY b.year

SELECT COUNT(b.year) 'browse_amount', b.year 'time_period' FROM ( select DATE_FORMAT(a.browse_time,'%Y%m') 'year' from statistics_product_browse a WHERE DATE_FORMAT(a.browse_time,'%Y') = #{year}
and a.member_id = #{memberId}
and a.product_id = #{productId}
) b GROUP BY b.year

SELECT CASE when (month(a.browse_time)=01 or month(a.browse_time)=02 or month(a.browse_time)=03 ) then CONCAT(year(a.browse_time),"年一季度") when (month(a.browse_time)=04 or month(a.browse_time)=05 or month(a.browse_time)=06 ) THEN CONCAT(year(a.browse_time),"年二季度") when (month(a.browse_time)=07 or month(a.browse_time)=08 or month(a.browse_time)=09 ) THEN CONCAT(year(a.browse_time),"年三季度") when (month(a.browse_time)=10 or month(a.browse_time)=11 or month(a.browse_time)=12 ) THEN CONCAT(year(a.browse_time),"年四季度") else "" end AS time_period,
CASE when (month(a.browse_time)=01 or month(a.browse_time)=02 or month(a.browse_time)=03 ) then CONCAT(year(a.browse_time),"01") when (month(a.browse_time)=04 or month(a.browse_time)=05 or month(a.browse_time)=06 ) THEN CONCAT(year(a.browse_time),"04") when (month(a.browse_time)=07 or month(a.browse_time)=08 or month(a.browse_time)=09 ) THEN CONCAT(year(a.browse_time),"07") when (month(a.browse_time)=10 or month(a.browse_time)=11 or month(a.browse_time)=12 ) THEN CONCAT(year(a.browse_time),"10") else "" end AS time_from,
CASE when (month(a.browse_time)=01 or month(a.browse_time)=02 or month(a.browse_time)=03 ) then CONCAT(year(a.browse_time),"03") when (month(a.browse_time)=04 or month(a.browse_time)=05 or month(a.browse_time)=06 ) THEN CONCAT(year(a.browse_time),"06") when (month(a.browse_time)=07 or month(a.browse_time)=08 or month(a.browse_time)=09 ) THEN CONCAT(year(a.browse_time),"09") when (month(a.browse_time)=10 or month(a.browse_time)=11 or month(a.browse_time)=12 ) THEN CONCAT(year(a.browse_time),"12") else "" end AS time_end,
count(1) 'browse_amount' FROM statistics_product_browse a where DATE_FORMAT(a.browse_time,'%Y')=#{year}
and a.member_id = #{memberId}
and a.product_id = #{productId}
GROUP BY time_period ORDER BY a.browse_time

SELECT COUNT(1) 'value', b.PROFESSION_SUP 'name' FROM statistics_product_browse a LEFT JOIN member b ON b.ID = a.member_id WHERE DATE_FORMAT(a.browse_time,'%Y') = #{year}
and a.member_id = #{memberId}
and a.product_id = #{productId}
GROUP BY b.PROFESSION_SUP

( SELECT count(1) 'value', b.ADDR_PROVINCE 'name' FROM statistics_product_browse a LEFT JOIN member b ON b.ID = a.member_id where DATE_FORMAT(a.browse_time,'%Y') = #{year}
and a.member_id = #{memberId}
and a.product_id = #{productId}
GROUP BY b.ADDR_PROVINCE ORDER BY value DESC LIMIT 10 ) union ( SELECT IFNULL(SUM(c.value),0) 'value', '其它' FROM ( SELECT count(1) 'value', b.ADDR_PROVINCE 'name' FROM statistics_product_browse a LEFT JOIN member b ON b.ID = a.member_id where DATE_FORMAT(a.browse_time,'%Y') = #{year}
and a.member_id = #{memberId}
and a.product_id = #{productId}
GROUP BY b.ADDR_PROVINCE ORDER BY value DESC LIMIT 10,68 )c )

SELECT COUNT(1) 'value', c.color_name 'name' FROM statistics_product_browse a LEFT JOIN product_record_color b ON b.product_id = a.product_id LEFT JOIN product_param_color c ON c.id = b.color_id WHERE DATE_FORMAT(a.browse_time,'%Y') = #{year}
and a.member_id = #{memberId}
and a.product_id = #{productId}
GROUP BY b.color_id

SELECT COUNT(1) 'value', c.style_name 'name' FROM statistics_product_browse a LEFT JOIN product_record_style b ON b.product_id = a.product_id LEFT JOIN product_param_style c ON c.id = b.style_id WHERE DATE_FORMAT(a.browse_time,'%Y') = #{year}
and a.member_id = #{memberId}
and a.product_id = #{productId}
GROUP BY b.style_id

SELECT COUNT(1) 'value', c.craft_name 'name' FROM statistics_product_browse a LEFT JOIN product b ON b.id = a.product_id LEFT JOIN product_param_craft c ON c.id = b.product_craft WHERE DATE_FORMAT(a.browse_time,'%Y') = #{year}
and a.member_id = #{memberId}
and a.product_id = #{productId}
GROUP BY b.product_craft

SELECT COUNT(1) 'value', c.material_name 'name' FROM statistics_product_browse a LEFT JOIN product b ON b.id = a.product_id LEFT JOIN product_param_material c ON c.id = b.product_material WHERE DATE_FORMAT(a.browse_time,'%Y') =#{year}
and a.member_id = #{memberId}
and a.product_id = #{productId}
GROUP BY b.product_material

SELECT COUNT(1) 'value', c.application_name 'name' FROM statistics_product_browse a LEFT JOIN product_record_application b ON b.product_id = a.product_id LEFT JOIN product_param_application c ON c.id = b.application_id WHERE DATE_FORMAT(a.browse_time,'%Y') = #{year}
and a.member_id = #{memberId}
and a.product_id = #{productId}
GROUP BY b.application_id

SELECT DATE_FORMAT(a.browse_time,'%Y%m%d') 'period', COUNT(1) 'amount' FROM statistics_product_browse a WHERE DATE_FORMAT(a.browse_time,'%Y%m') IN
${item}
AND a.product_id = #{pd.productId}
AND a.member_id = #{pd.memberId}
GROUP BY DATE_FORMAT(a.browse_time,'%Y%m%d') ORDER BY DATE_FORMAT(a.browse_time,'%Y%m%d')

SELECT count(1) 'value', b.PROFESSION_SUP 'name', b.PROFESSION_SUB 'name1' FROM statistics_product_browse a LEFT JOIN member b ON b.ID = a.member_id WHERE DATE_FORMAT(a.browse_time,'%Y') = #{pd.year}
and a.member_id = #{pd.memberId}
and a.product_id = #{pd.productId}
GROUP BY b.PROFESSION_SUB

SELECT count(1) 'value', b.ADDR_PROVINCE 'name' FROM statistics_product_browse a LEFT JOIN member b ON b.ID = a.member_id WHERE DATE_FORMAT(a.browse_time,'%Y') = #{pd.year}
and a.member_id = #{pd.memberId}
and a.product_id = #{pd.productId}
GROUP BY b.ADDR_PROVINCE
